document.addEventListener("DOMContentLoaded",function(){const themeToggle=document.querySelector(".theme-toggle");const prefersDarkScheme=window.matchMedia("(prefers-color-scheme: dark)");function applyTheme(theme){if(theme==="light"){document.documentElement.setAttribute("data-theme","light");console.log("Tema aplicado: claro")}else{document.documentElement.removeAttribute("data-theme");console.log("Tema aplicado: oscuro")}updateToggleButton(theme)}function updateToggleButton(theme){if(themeToggle){}}function getInitialTheme(){const savedTheme=localStorage.getItem("paellaTheme");if(savedTheme){return savedTheme}else{return prefersDarkScheme.matches?"dark":"light"}}const initialTheme=getInitialTheme();applyTheme(initialTheme);if(themeToggle){themeToggle.addEventListener("click",function(){const currentTheme=document.documentElement.hasAttribute("data-theme")?"light":"dark";const newTheme=currentTheme==="light"?"dark":"light";localStorage.setItem("paellaTheme",newTheme);applyTheme(newTheme);console.log(`Cambiando a tema ${newTheme}`)})}prefersDarkScheme.addEventListener("change",function(e){if(!localStorage.getItem("paellaTheme")){const newTheme=e.matches?"dark":"light";applyTheme(newTheme);console.log(`Preferencia del sistema cambi√≥ a ${newTheme}`)}});window.addEventListener("pageshow",function(event){if(event.persisted){const currentStoredTheme=localStorage.getItem("paellaTheme")||(prefersDarkScheme.matches?"dark":"light");applyTheme(currentStoredTheme);console.log("Tema reaplicado desde bfcache")}})});